					return [{"depth":2,"slug":"7-common-window-installation-mistakes--how-we-avoid-them-in-bishops-stortford","text":"7 Common Window Installation Mistakes & How We Avoid Them in Bishop’s Stortford"},{"depth":2,"slug":"mistake-1-inaccurate-measuring","text":"Mistake #1: Inaccurate Measuring"},{"depth":2,"slug":"mistake-2-not-installing-the-window-plumb-level-and-square","text":"Mistake #2: Not Installing the Window Plumb, Level, and Square"},{"depth":2,"slug":"mistake-3-improper-sealing-and-insulation","text":"Mistake #3: Improper Sealing and Insulation"},{"depth":2,"slug":"mistake-4-failing-to-secure-the-frame-correctly","text":"Mistake #4: Failing to Secure the Frame Correctly"},{"depth":2,"slug":"mistake-5-damaging-surrounding-plaster-and-brickwork","text":"Mistake #5: Damaging Surrounding Plaster and Brickwork"},{"depth":2,"slug":"mistake-6-ignoring-building-regulations-part-l","text":"Mistake #6: Ignoring Building Regulations (Part L)"},{"depth":2,"slug":"mistake-7-lack-of-post-installation-checks","text":"Mistake #7: Lack of Post-Installation Checks"},{"depth":2,"slug":"frequently-asked-questions","text":"Frequently Asked Questions"},{"depth":3,"slug":"what-is-the-most-common-window-installation-mistake","text":"What is the most common window installation mistake?"},{"depth":3,"slug":"how-can-i-tell-if-my-windows-have-been-installed-poorly","text":"How can I tell if my windows have been installed poorly?"},{"depth":3,"slug":"can-a-bad-window-installation-be-fixed","text":"Can a bad window installation be fixed?"},{"depth":3,"slug":"does-a-fensa-certificate-guarantee-a-perfect-installation","text":"Does a FENSA certificate guarantee a perfect installation?"},{"depth":2,"slug":"your-guarantee-of-a-quality-installation","text":"Your Guarantee of a Quality Installation"}];
